  <input type="text" [(ngModel)]="input" placeholder="Enter phrase">
  <br><br>
  <button (click)="submit()">Submit</button>    
</div>

<div class="isoscelesTriangle" [style.border-left]="borderLeft" [style.border-right]="borderRight" [style.border-bottom]="borderBottom"></div>
{{width}} {{height}} {{radius}} {{shape}} {{length}}
<div class="circle" [ngStyle]="{height: radius, width: radius}">
</div>
<div [class]="shape" [ngStyle]="bindCSS()"></div>

