import { Component, EventEmitter, Output } from '@angular/core';
import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Injectable()
export class ConfigService {
  constructor(private http: HttpClient) { }
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  template: `
  <div [style]="css2()">
    ...
  </div>
`
})
export class HomeComponent {
  constructor(private http: HttpClient) {

  }
  shape = "";
  width = 0;
  height = 0;
  radius = 0;
  length = 0;

  public bindShape(event: any) {
    if(event.shape != null || undefined){
      this.shape = event.shape;
    }
    if(event.width != null || undefined){
      this.width = event.width;
    }
    if(event.height != null || undefined){
      this.height = event.height;
    }
    if(event.radius != null || undefined){
      this.radius = event.radius;
    }
    if(event.length != null || undefined){
      this.length = event.length;
    }
  }
  css = "'width':"+ this.width +", 'height': "+this.height+", background: '#f4f4f4';"
  borderLeft = (this.width / 2) + "px solid transparent";
  borderRight = (this.width / 2) + "px solid transparent";
  borderBottom = this.height + "px solid #0d6efd";
  public css2(){
    return {
      'width': '100px',
      'height': '100px',
      'background-color': '#0d6efd'
    };
  }
  public bindCSS(data: any){
    ///switch case for shape
    switch(data.shape){
      case "isosceles triangle":
          this.borderLeft = (data.width / 2) + "px solid transparent";
          this.borderRight = (data.width / 2) + "px solid transparent";
          this.borderBottom = data.height + "px solid #0d6efd";
          console.log(this.width + " " + this.height)
          break;
      case "square":
        // return {
        //   'width': this.width,
        //   'height': this.height,
        //   'background-color': '#0d6efd'
        // };
      case "Scalene Triangle":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.height + ' solid transparent',
        //   'border-bottom': this.length + ' solid #0d6efd'
        // };
      case "Parallelogram":
        // return {
        //   'width': this.width,
        //   'height': this.height,
        //   'background-color': '#0d6efd',
        //   'transform': 'skewX(20deg)'
        // };
      case "Equilateral Triangle":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.width + ' solid transparent',
        //   'border-bottom': this.height + ' solid #0d6efd'
        // };
      case "Pentagon":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-bottom': this.height + ' solid #0d6efd',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.width + ' solid transparent',
        //   'transform': 'rotate(36deg)',
        //   'position': 'relative',
        //   'left': '-10px'
        // };
      case "Rectangle":
        // return {
        //   'width': this.width,
        //   'height': this.height,
        //   'background-color': '#0d6efd'
        // };
      case "Hexagon":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-bottom': this.height + ' solid #0d6efd',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.width + ' solid transparent',
        //   'transform': 'rotate(90deg)',
        //   'position': 'relative',
        //   'left': '-10px'
        // };
      case "Heptagon":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-bottom': this.height + ' solid #0d6efd',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.width + ' solid transparent',
        //   'transform': 'rotate(90deg)',
        //   'position': 'relative',
        //   'left': '-10px'
        // };
      case "Octagon":
        // return {
        //   'width': '0',
        //   'height': '0',
        //   'border-bottom': this.height + ' solid #0d6efd',
        //   'border-left': this.width + ' solid transparent',
        //   'border-right': this.width + ' solid transparent',
        //   'transform': 'rotate(90deg)',
        //   'position': 'relative',
        //   'left': '-10px'
        // };
      case "Circle":
        this.width = data.width;
        this.height = data.height;
        break;
      case "Oval":
        // return {
        //   'width': this.width,
        //   'height': this.height,
        //   'border-radius': '50%',
        //   'background-color': '#0d6efd'
        // };
      default:
        break;
    }
  }
  public submit(){
    ///create a post request to the https://localhost:44420/
    //with the input as the body

    let _headers: HttpHeaders = new HttpHeaders({
      'Content-Type': 'application/json' 
    });

    //create a json object
    let json = JSON.stringify(this.input);

    // this.http.post('https://localhost:44420/weatherforecast/Input', json, {headers:_headers}).subscribe((data) => {
    //   console.log(data + " top");
    // });
    ///create a post request to https://localhost:44420/weatherforecast/Input with input as the body
    fetch('https://localhost:44420/weatherforecast/Input', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(this.input)
      })
      .then(response => response.json())
      .then(data => {
        console.log(data);
        this.bindShape(data);
        this.bindCSS(data);
      })
      .catch(rejected => {
          alert(rejected);
      });        
    }
    input="";
}


